import React from 'react';
import 'froala-editor/js/froala_editor.pkgd.min.js';
import 'froala-editor/css/froala_style.min.css';
import 'froala-editor/css/froala_editor.pkgd.min.css';
import FroalaEditor from 'react-froala-wysiwyg';
import axios from 'axios';

const config={

    charCounterCount: false,
    model:{},
    events : {
        'froalaEditor.focus' : function(e, editor) {
        console.log(editor.selection.get());
            }
        }
    }

class Editor extends React.Component{

    state ={
        title:'',
        description:''
    }

    inputChangeHandler=(event)=>{
        this.setState({title:event.target.value})
    }

    descriptionChangeHandler=(event)=>{
        this.setState({description:event})
    }

    saveData=()=>{
        let obj = {
            title : this.state.title,
            description : this.state.description
        }
        axios.post('https://todo-34b9f.firebaseio.com/page.json',obj)
        .then(response =>{
            console.log(response);
            console.log("Successfully Saved...");
        }).catch(error => {
            console.log(error);
            console.log("Failed...");
        })
        alert("Successfully Added");
        // this.props.history.goBack();
    }
    render(){
        return(
            <>
            <h3> Title </h3>

            <Input type="text" onChange={this.inputChangeHandler}/><br/><br/><br/>
             <FroalaEditor
            tag='textarea'
            config={config}
            onModelChange={this.descriptionChangeHandler}
            />
            <br/>
            <button type="button" class="btn btn-primary " onClick={this.saveData}>Save</button>

            </>
        )
    }
}

export default Editor;
