import React from 'react';
import 'froala-editor/js/froala_editor.pkgd.min.js';
import 'froala-editor/css/froala_style.min.css';
import 'froala-editor/css/froala_editor.pkgd.min.css';
import FroalaEditor from 'react-froala-wysiwyg';
import axios from 'axios';
import { Input } from 'semantic-ui-react';
import { withRouter } from 'react-router-dom';

const config={

    charCounterCount: false,
    model:{},
    events : {
        'froalaEditor.focus' : function(e, editor) {
        console.log(editor.selection.get());
            }
        }
    }

class Editor extends React.Component{

    state ={
        title:'',
        description:''
    }

    componentDidMount = (props) => {

        if(this.props.match.params.id !== 'post'){
            console.log(this.props.match.params.id);
            axios.get(`https://todo-34b9f.firebaseio.com/page/${this.props.match.params.id}.json`)
            .then(response => {
                this.setState({title:response.data.title,description:response.data.description})
                console.log(response);

            })
            .catch(error => {
                console.log(error)
            })
        }
      }
    inputChangeHandler=(event)=>{
        this.setState({title:event.target.value})
    }

    descriptionChangeHandler=(event)=>{
        this.setState({description:event})
    }

    saveData=()=>{
        let obj = {
            title : this.state.title,
            description : this.state.description
        }
        axios.post('https://todo-34b9f.firebaseio.com/page.json',obj)
        .then(response =>{
            console.log(response);
            console.log("Successfully Saved...");
        }).catch(error => {
            console.log(error);
            console.log("Failed...");
        })
        alert("Successfully Added");
        this.props.history.push('/posts');
    }

    saveEditData=(id)=>{
        let obj ={
            title:this.state.title,
            description:this.state.description
        }
        axios.patch(`https://todo-34b9f.firebaseio.com/page/${this.props.match.params.id}.json`,obj)

    }
    render(){
        return(
            <>
            <h3> Title </h3>

            <Input type="text" 
            onChange={this.inputChangeHandler}
            value={this.state.title}/><br/><br/><br/>
             <FroalaEditor
            tag='textarea'
            model={this.state.description}
            config={config}
            onModelChange={this.descriptionChangeHandler}
            />
            <br/>
            {this.props.match.params.id !== 'post'?
             <button type="button" class="btn btn-primary " onClick={this.saveEditData}>Edit </button>:
            <button type="button" class="btn btn-primary " onClick={this.saveData}>Save</button>
        }

            </>
        )
    }
}

export default withRouter(Editor);
