import React from 'react';
import FetchApi from '../utility/fetchApi';

class Projects extends React.Component{

    state ={
        projects:[]
    }
    showProject=()=>{
        console.log("state : ",this.state.projects);

    }
    componentDidMount(){

        let url = localStorage.getItem('url');
        let listOfProjects = [];
        let projectLength=0;
        FetchApi.callApi(`${url}/rest/api/3/project`)
        .then(res =>{
            // console.log("res",res);
            // console.log("res length : ",res.length);
            projectLength = res.length;
            for(let i=0;i<res.length;i++){
                console.log(res[i].key);
                listOfProjects.push(res[i].key);
                console.log(res[i].name);
            }
            this.setState({projects:[...this.state.projects,listOfProjects]})
            //this.showProject();
        }).catch(error =>{
            console.log(error);
        })
    }
    render(){
        debugger;
        let project;
        project =
        this.state.projects.map(id => (
            <button>{id}</button>
        ))



        return(
            <>
            <h2>List of Projects</h2>
            {project}
            </>
        )
    }
}

export default Projects;
