import React from 'react';
import { Table } from 'semantic-ui-react';
import Axios from 'axios';
import { withRouter,Route} from 'react-router-dom';
import Editor from './editor';

class Home extends React.Component{

    state={
        fullData:[],
        page:[]
    }

    componentDidMount(){
        Axios.get('https://todo-34b9f.firebaseio.com/page.json')
        .then(response => {
            console.log(response);
            const fetchedOrders=[];
                     for(let key in response.data) {
                         fetchedOrders.push({
                             ...response.data[key],
                             id:key
                         });
                     }
                     console.log("fetched Api",fetchedOrders)
                     this.setState({fullData:fetchedOrders})
        }).catch(error =>{
            console.log(error);
        })
    }

    addPagehandler=()=>{
        console.log("add post");
        this.props.history.push('/post/add');
    }

    deleteHandler=(id)=>{
        console.log("id to delete : ",id);
        Axios.delete(`https://todo-34b9f.firebaseio.com/page/${id}.json`)
        .then(response => {
            Axios.get('https://todo-34b9f.firebaseio.com/page.json')
            .then(res => {
                const fetchedOrders=[];
            for(let key in response.data) {
                fetchedOrders.push({
                    ...response.data[key],
                    id:key
                });
            }
            console.log("fetched Api",fetchedOrders)
            this.setState({fullData:fetchedOrders})
            console.log("Successfully deleted")
            })

        }).catch(error=>{
            console.log(error);
        })
    }

    editHandler=(id) =>{
        this.props.history.push(`/post/${id}`)
    }

    readHandler=(id)=>{
        this.props.history.push(`/post/preview/${id}`)
    }

    add=()=>{
        this.props.history.push('/go');
    }


    render(){
        let page;

        page=
        <table class="table table-striped table-dark">
            <Table.Header>
            <tr >
                <td><b>Title</b></td>
                <td><b>Actions</b></td>
            </tr>
            </Table.Header>

            {this.state.fullData.map(perPage => (
               <tr>
                   <td >{perPage.title}</td>
                   <button type="button" class="btn btn-primary " onClick={(id) =>this.readHandler(perPage.id)}>Read</button>
                   <button type="button" class="btn btn-warning " onClick={(id) =>this.editHandler(perPage.id)}>Edit</button>
                   <button type="button" class="btn btn-danger "  onClick={(id) =>this.deleteHandler(perPage.id)}>Delete</button>
               </tr>
            ))}
          </table>

       return(
            <>
            <h1> Memo </h1>
            <div class="text-right">
            <button type="button" class="btn btn-primary " onClick={this.addPagehandler}>Add</button><br/><br/>
            <button type="button" class="btn btn-primary " onClick={this.add}>S</button></div><br/><br/>

            {page}
            </>
        )
    }
}

export default withRouter(Home);
