import React, { PureComponent } from 'react';
import {PieChart,Pie,Cell} from 'recharts'
//import Axios from '../../axios-orders';

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];
const RADIAN = Math.PI / 180;

export default class Chart extends PureComponent {

    state={
        array:[]
    }

    constructor(props){
        super(props);
        this.state = {
            array:[]
        }
        // console.log("props data: ",this.props.data);
        // this.setState({array:this.props.data});
        // console.log("array : ",this.state.array);

    }

	componentDidMount(){
            console.log("this.state.array : ",this.props.data);
            this.setState({array : this.props.data});
            console.log("actaul array : ",this.state.array);
		//this.chartForAuthor();

    }

    renderCustomizedLabel = ({
		cx, cy, midAngle, innerRadius, outerRadius, percent, index,
	}) => {
		const radius = innerRadius + (outerRadius - innerRadius) * 0.5;
		const x = cx + radius * Math.cos(-midAngle * RADIAN);
		const y = cy + radius * Math.sin(-midAngle * RADIAN);

		return (
			<text x={x} y={y} fill="white" textAnchor={x > cx ? 'start' : 'end'} dominantBaseline="central">
				{`${(percent * 100).toFixed(0)}%`}
			</text>
		);
	};

	render() {

		return (
            <>
            <p>{this.props.count}</p>
            <p>{this.props.count2}</p>
			 <PieChart width={400} height={400}>
				<Pie
					data={this.props.data}
					cx={300}
					cy={200}
					labelLine={false}
					label={this.renderCustomizedLabel}
					outerRadius={80}
					fill="#8884d8"
					dataKey="value"
				>
					{
						this.props.data.map((entry, index) => <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />)
					}
				</Pie>
			</PieChart> 
            </>
		);
	}
}
