class FetchData extends React.Component{

    state ={
        usersArray:{},
        newArray:[],
        arrayOfData:[]

    }

    componentDidMount(){
        let array=[];
        let storyPoint=0;
       // https://madhvi.atlassian.net/rest/api/2/user/assignable/search?project=REAC
        superagent
            .get('https://madhvi.atlassian.net/rest/api/2/user/assignable/search?project=REAC')
            .set('Access-Control-Allow-Credentials', '*')
            .set('Accept', 'application/json')
            .set('Authorization', 'Basic bWFkaHZpa29raWxAZ21haWwuY29tOm1TOVlqRk43TjkyRUUzTHFYMGtkQjNBOA==')

           .end((err, res) => {
                if (err) { return console.log("eeeeee : ",err); }
                this.setState({usersArray:res.body})
                //console.log(res.body);
                let s = Object.keys(this.state.usersArray).length;

                for(let i=0;i<s;i++){

                    array.push(this.state.usersArray[i].key); 
               superagent
              .get(`https://madhvi.atlassian.net/rest/api/3/search?jql=assignee=${array[i]}`)
              .set('Access-Control-Allow-Credentials', '*')
              .set('Accept', 'application/json')
              .set('Authorization', 'Basic bWFkaHZpa29raWxAZ21haWwuY29tOm1TOVlqRk43TjkyRUUzTHFYMGtkQjNBOA==')

             .end((err, res) => {
                  if (err) { return console.log("eeeeee : ",err); }
                 console.log("arrays here : ",this.state.newArray);
                console.log("response : ",res.body);
                 this.state.newArray.push(res.body);

                // this.setState({array:this.state.newArray})
                debugger;
                console.log("new Array : ",this.state.newArray);
               this.setState({arrayOfData:[...this.state.arrayOfData, res.body]});



                //  this.setState([arrayOfData:this.state.newArray);
                // let s = Object.keys(res.body.issues).length;
                // console.log("s : ",s);
                // for(let i=0;i<s;i++){
                //     storyPoint = storyPoint +  res.body.issues[i].fields.customfield_10024;

                // }
                // 
                //  console.log("story point : ",storyPoint);
                //  obj.storyPoints=storyPoint;
                //  console.log("object:",obj);

                })
               }
              },
            );
            console.log("shown array : ",this.state.newArray);
            console.log("Actual Array : ",this.state.arrayOfData);
    }


    render(){
        // debugger;

       let posts = 

               <table border="1" width="80%">
                   <tbody>
                   <tr>
                       <td><b>Title</b></td>
                        <td><b>Type</b></td>
                       <td><b></b></td>
                       <td><b></b></td>


                   </tr> 

                 {this.state.arrayOfData.map(l => (      //array to array of JSX
                    <tr>  

                        <td>{l[0].total}</td>
                        <td>{l.issues}</td>
                        <td>{l.maxResult}</td>
                        <td>{l.updatedDate} </td>
                    </tr>  
                ))}
                </tbody>
                </table>

        //console.log("arrayss : ",this.state.arrays);
        return(
            <>
            <h1>hello</h1>
           {posts}


            </>
        )
    }
}

export default FetchData;
